mapper.select:
select * from (select i.*,b.quote_body_id,b.item_code,b.price,b.quantity, (select v.customer_name from sale_customer v where v. customer_id = i. customer_id) as customer_name, (select item_name from pro_production p where p.item_code = b.item_code and p.status=0 ) as item_name from sale_quote i left join sale_quote_body b on i.quote_id = b.quote_id where i.status = 0 )tt where 1=1
and tt.item_code like CONCAT('%',#{searchValue},'%' ) or tt.item_name like CONCAT('%',#{searchValue},'%' ) or tt.customer_name like CONCAT('%',#{searchValue},'%' )
and tt.create_by= #{createBy}

select CONCAT_WS(" - ", min(b.price), max(b.price)) as min_max_price
from sale_quote q left join sale_quote_body b on q.quote_id = b.quote_id
where q.quote_date > DATE_ADD(SYSDATE(),INTERVAL -3 MONTH) and b.item_code = #{itemCode}

   and q.create_by = #{createBy}

GROUP BY b.item_code

SELECT *
FROM sale_quote, sale_customer
WHERE sale_quote.customer_id = sale_customer.customer_id and  quote_id=#{quoteId}

mapper.update:
update sale_quote
customer_id = #{customerId}, reminder = #{reminder}, quote_date = #{quoteDate}, remark = #{remark}, update_by = #{updateBy}, update_time = sysdate()
where quote_id=#{quoteId}
